package com.example.app.servicebestpractice;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;/** * Created by shaomiao on 2016-12-11. */public class AlarmReceiver extends BroadcastReceiver {    @Override    public void onReceive(Context context, Intent intent) {        Intent i = new Intent(context, LongRunningService.class);        context.startService(i);    }}